body.script:
        function changeStyle(){
            var msgObj = document.getElementById('J_404_msg'),
                msgs = ['sorry，页面去旅游了，请先休息一下吧^_^!', 'sorry，页面被打劫了，先休息一下吧^_^!'];

            var randomNumber = function(m,n){
                return Math.round(Math.random()*(n-m)+m);
            }

            var idx = randomNumber(1,2);

            msgObj.className = 'msg-' + idx;
            msgObj.innerHTML = msgs[idx-1];     
        }

        window.onload = changeStyle();

        setTimeout('window.location="//ac.qq.com"', 3000);
