meta.script:
/*
 * SPDX-FileCopyrightText: syuilo and misskey-project
 * SPDX-License-Identifier: AGPL-3.0-only
 */ 'use strict';
// ブロックの中に入れないと、定義した変数がブラウザのグローバルスコープに登録されてしまい邪魔なので
(async ()=>{
    window.onerror = (e)=>{
        console.error(e);
        renderError('SOMETHING_HAPPENED', e);
    };
    window.onunhandledrejection = (e)=>{
        console.error(e);
        renderError('SOMETHING_HAPPENED_IN_PROMISE', e);
    };
    let forceError = localStorage.getItem('forceError');
    if (forceError != null) {
        renderError('FORCED_ERROR', 'This error is forced by having forceError in local storage.');
        return;
    }
    if (localStorage.getItem('id') === null) {
        localStorage.setItem('id', crypto.randomUUID().replaceAll('-', ''));
    }
    let id = localStorage.getItem('id');
    //#region Detect language & fetch translations
    if (!Object.hasOwn(localStorage, 'locale')) {
        let lang = localStorage.getItem('lang');
        if (lang == null || lang.toString == null || lang.toString() === 'null') {
            lang = 'ja-JP';
        }
        const metaRes = await window.fetch('/api/meta', {
            method: 'GET',
            credentials: 'omit',
            headers: {
                'Content-Type': 'application/json',
                'X-Client-Transaction-Id': `${id}-misskey-${crypto.randomUUID().replaceAll('-', '')}`
            }
        });
        if (metaRes.status !== 200) {
            renderError('META_FETCH');
            return;
        }
        const meta = await metaRes.json();
        const v = meta.version;
        if (v == null) {
            renderError('META_FETCH_V');
            return;
        }
        const localRes = await window.fetch(`/assets/locales/${lang}.${v}.json`);
        if (localRes.status === 200) {
            localStorage.setItem('lang', lang);
            localStorage.setItem('locale', await localRes.text());
            localStorage.setItem('localeVersion', v);
        } else {
            renderError('LOCALE_FETCH');
            return;
        }
    }
    //#endregion
    //#region Script
    async function importAppScript() {
        await import(`/vite/${CLIENT_ENTRY}`).catch(async (e)=>{
            console.error(e);
            renderError('APP_IMPORT', e);
        });
    }
    // タイミングによっては、この時点でDOMの構築が済んでいる場合とそうでない場合とがある
    if (document.readyState !== 'loading') {
        importAppScript();
    } else {
        window.addEventListener('DOMContentLoaded', ()=>{
            importAppScript();
        });
    }
    //#endregion
    //#region Theme
    const theme = localStorage.getItem('theme');
    if (theme) {
        for (const [k, v] of Object.entries(JSON.parse(theme))){
            document.documentElement.style.setProperty(`--MI_THEME-${k}`, v.toString());
            // HTMLの theme-color 適用
            if (k === 'htmlThemeColor') {
                for (const tag of document.head.children){
                    if (tag.tagName === 'META' && tag.getAttribute('name') === 'theme-color') {
                        tag.setAttribute('content', v);
                        break;
                    }
                }
            }
        }
    }
    const colorScheme = localStorage.getItem('colorScheme');
    if (colorScheme) {
        document.documentElement.style.setProperty('color-scheme', colorScheme);
    }
    //#endregion
    const fontSize = localStorage.getItem('fontSize');
    if (fontSize) {
        document.documentElement.classList.add('f-' + fontSize);
    }
    const useSystemFont = localStorage.getItem('useSystemFont');
    if (useSystemFont) {
        document.documentElement.classList.add('useSystemFont');
    }
    const customCss = localStorage.getItem('customCss');
    if (customCss && customCss.length > 0) {
        const style = document.createElement('style');
        style.innerHTML = customCss;
        document.head.appendChild(style);
    }
    async function addStyle(styleText) {
        let css = document.createElement('style');
        css.appendChild(document.createTextNode(styleText));
        document.head.appendChild(css);
    }
    async function renderError(code, details) {
        // Cannot set property 'innerHTML' of null を回避
        if (document.readyState === 'loading') {
            await new Promise((resolve)=>window.addEventListener('DOMContentLoaded', resolve));
        }
        const locale = JSON.parse(localStorage.getItem('locale') || '{}');
        const messages = Object.assign({
            title: 'Failed to initialize Misskey',
            solution: 'The following actions may solve the problem.',
            solution1: 'Update your os and browser',
            solution2: 'Disable an adblocker',
            solution3: 'Clear the browser cache',
            solution4: '(Tor Browser) Set dom.webaudio.enabled to true',
            otherOption: 'Other options',
            otherOption1: 'Clear preferences and cache',
            otherOption2: 'Start the simple client',
            otherOption3: 'Start the repair tool'
        }, locale?._bootErrors || {});
        const reload = locale?.reload || 'Reload';
        let errorsElement = document.getElementById('errors');
        if (!errorsElement) {
            document.body.innerHTML = `






${messages.title}

				${reload}


${messages.solution}

${messages.solution1}

${messages.solution2}

${messages.solution3}

${messages.solution4}

				${messages.otherOption}


						${messages.otherOption1}






						${messages.otherOption2}






						${messages.otherOption3}







			`;
            document.getElementById("reload").addEventListener('click', ()=>{
                location.reload();
            });
            errorsElement = document.getElementById('errors');
        }
        const detailsElement = document.createElement('details');
        detailsElement.id = 'errorInfo';
        detailsElement.innerHTML = `



			ERROR CODE: ${code}

		${details.toString()} ${JSON.stringify(details)}`;
        errorsElement.appendChild(detailsElement);
        addStyle(`
		* {
			font-family: BIZ UDGothic, Roboto, HelveticaNeue, Arial, sans-serif;
		}

		#misskey_app,
		#splash {
			display: none !important;
		}

		body,
		html {
			background-color: #222;
			color: #dfddcc;
			justify-content: center;
			margin: auto;
			padding: 10px;
			text-align: center;
		}

		button {
			border-radius: 999px;
			padding: 0px 12px 0px 12px;
			border: none;
			cursor: pointer;
			margin-bottom: 12px;
		}

		.button-big {
			background: linear-gradient(90deg, rgb(134, 179, 0), rgb(74, 179, 0));
			line-height: 50px;
		}

		.button-big:hover {
			background: rgb(153, 204, 0);
		}

		.button-small {
			background: #444;
			line-height: 40px;
		}

		.button-small:hover {
			background: #555;
		}

		.button-label-big {
			color: #222;
			font-weight: bold;
			font-size: 1.2em;
			padding: 12px;
		}

		.button-label-small {
			color: rgb(153, 204, 0);
			font-size: 16px;
			padding: 12px;
		}

		a {
			color: rgb(134, 179, 0);
			text-decoration: none;
		}

		p,
		li {
			font-size: 16px;
		}

		.icon-warning {
			color: #dec340;
			height: 4rem;
			padding-top: 2rem;
		}

		h1 {
			font-size: 1.5em;
			margin: 1em;
		}

		code {
			font-family: Fira, FiraCode, monospace;
		}

		#errorInfo {
			background: #333;
			margin-bottom: 2rem;
			padding: 0.5rem 1rem;
			width: 40rem;
			border-radius: 10px;
			justify-content: center;
			margin: auto;
		}

		#errorInfo summary {
			cursor: pointer;
		}

		#errorInfo summary > * {
			display: inline;
		}

		@media screen and (max-width: 500px) {
			#errorInfo {
				width: 50%;
			}
		}`);
    }
})();
